import React, { Component } from 'react';
import { Banner } from './Banner';
import { Table } from './TableBooks';

export default class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listBook: [{ book: "Programa en C", borrowed: false },
                { book: "Master en DEV", borrowed: false },
                { book: "Aprende JS", borrowed: true },
                { book: "Arregla Visual Studio", borrowed: false }],
        }
    }

    render() {
        return (
            <>
                <Banner />
                <table className="table table-striped table-bordered">
                    <thead>
                        <tr><th>Nombre</th><th>Prestamo</th></tr>
                    </thead>
                    <tbody>
                        <Table/>
                    </tbody>
                </table>
            </>
            )
    }
}
