import React, { Component } from 'react';

export class Editor extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "Javier",
            flavor: "vanilla",
            toppings: ["strawberries"]
        }
        this.flavors = ["chocolate", "double Chocolate", "TripleChocolate", "Vainilla"];
        this.toppings = ["Sprinkles", "fudge sauce","maple Syrup"]
    }

    updateFormValue = (event) => {
        this.setState({ [event.target.name]: event.target.value },
            ()=> this.props.submit(this.state))
    }

    updateFormValueOption = (event) => {
        let options = [...event.target.options].filter(o => o.selected).map(o => o.value);
        this.setState({ [event.target.name]: options },
            () => this.props.submit(this.state))
    }

    render() {
        return (
            <div className="h5 bg-info text-white p-2">
                <div className="form-group">
                    <label>Name:</label>
                    <input className="form-control"
                        name="name"
                        value={this.state.name}
                        onChange={this.updateFormValue}
                    />
                </div>
                <div className="form-group">
                    <label>Ice Scream Flavors</label>
                    <select className="form-control"
                        name="flavor" value={this.state.flavor}
                        onChange={this.updateFormValue}>
                        {this.flavors.map(flavor =>
                            <option value={flavor} key={flavor}>
                                {flavor}
                            </option>
                        )}
                        
                    </select>
                </div>
                <div>
                    <label>Ice Scream Toppings:</label>
                    <select multiple={true} className="form-control"
                        name="toppings" value={this.state.toppings}
                        onChange={this.updateFormValueOption}>
                        {this.toppings.map(toppings =>
                            <option value={toppings} key={toppings}>
                            {toppings}
                            </option>
                        )}

                    </select>
                </div>
            </div>
            )
    }
}
