import React, { Component } from 'react';

export class Editor extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name:""
        }
    }


    render() {
        return (
            <div className="h5 bg-info text-white p-2">
                <div className="form-group">
                    <label>Name:</label>
                    <input className="form-control"
                        name="name"
                        value={this.state.name}
                    />
                </div>
            </div>
            )
    }
}
