import React from 'react';
import { ValidationContex } from './ValidationContext';

export class ValidationMessage extends Component {
    static contexType = ValidationContext;

    render() {
        return this.context.getMessagesForField(this.props.field).map(err =>
            <div className="small bg-danger text-withe mt-1 p-1"
                key={err}>
                {err}
            </div>
            )
    }
}
