        super(props);
    }

    render() {
        return <div className="container-fluid">
            <div className="row p-2">
                <div className="col-6 p-2">
                    <Editor/>
                </div>
                <div className="col-6 p-2">
                    <Display/>
                </div>
            </div>
        </div>
    }
}
